INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (3, 3, '978-88-04-66795-7', 'Il cavaliere inesistente', 'Narrativa', 1, 'Mondadori', '"Il cavaliere inesistente" di Italo Calvino segue le gesta di Agilulfo, un cavaliere che non esiste fisicamente ma incarna l''ideale cavalleresco. La storia esplora temi di onore, identità e realtà in un contesto fantastico-medievale.', 'assets/thumbnails/3.png', 3);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (3, 3, '978-88-04-63713-4', 'Il sentiero dei nidi di ragno', 'Narrativa', 1, 'Mondadori', '"Il sentiero dei nidi di ragno" di Italo Calvino segue la crescita di Pin, un ragazzo durante la Seconda Guerra Mondiale. Ambientato in Liguria, il romanzo esplora l''infanzia, la guerra e la ricerca di significato in un mondo in tumulto.', 'assets/thumbnails/4.png', 4);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '9788893817035', 'Harry Potter e la camera dei segreti', 'Narrativa', 1, 'SALANI EDITORE', 'Harry Potter e la camera dei segreti (titolo originale in inglese: Harry Potter and the Chamber of Secrets) è il secondo romanzo della saga high fantasy Harry Potter, scritta da J. K. Rowling ', 'assets/thumbnails/5.png', 5);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (3, 3, '9788804598893', 'Il barone rampante', 'Narrativa', 1, 'Mondadori', '“Il barone rampante” racconta la vita Cosimo Piovasco di Rondò, primogenito del barone di Rondò. Cosimo il 15 giugno 1767, dopo un litigio con il padre causato dalla stanchezza di dover obbedire alle pretese dei genitori e dai maltrattamenti della sorella Battista, decide di rifugiarsi sugli alberi.', 'assets/thumbnails/6.png', 6);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '9788893817066', 'Harry Potter e l''Ordine della Fenice', 'fantasy', 1, 'salani', 'harry potter', 'assets/thumbnails/7.png', 7);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 4, '9788826816586', 'I Promessi Sposi', 'Narrativa ', 1, 'Atlas', 'I Promessi sposi sono un romanzo storico ambientato nella Lombardia del 1628-1630, che ha per protagonisti i giovani Renzo Tramaglino e Lucia Mondella il cui matrimonio viene impedito dal signorotto del loro paese, don Rodrigo, a causa di una futile scommessa col cugino Attilio.', 'assets/thumbnails/8.png', 8);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (5, 5, 'privo01', 'Il Mulino Di Po', 'Romanzo', 1, 'Arnoldo Mondatori', 'Romanzo Storico', 'assets/thumbnails/9.png', 9);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '9788893817059', 'Harry Potter e il calice di fuoco', 'NARRATIVA', 1, 'SALANI EDITORE', 'un momento cruciale nella vita di Harry Potter: ormai è un mago adolescente, vuole andarsene dalla casa dei pestiferi Dursley, vuole sognare la cercatrice del Corvonero per cui ha una cotta tremenda... E poi vuole scoprire quali sono i grandiosi avvenimenti che si terranno a Hogwarts e che riguarderanno altre due scuole di magia e una grande competizione che non si svolge da cento anni. Harry Potter vuole davvero essere un normale mago di quattordici anni. Ma sfortunatamente, Harry non è un mago normale. E stavolta la differenza può essergli fatale. Età di lettura: da 8 anni.', 'assets/thumbnails/11.png', 11);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (5, 7, '8804116382', 'Ernest Hemingway', 'Romanzo', 1, 'Arnoldo Mondatori', 'Romanzi e Racconti', 'assets/thumbnails/12.png', 12);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '9788893817028', 'Harry Potter e la pietra filosofale', 'NARRATIVA', 1, 'SALANI EDITORE', 'Harry Potter e la Pietra Filosofale della scrittrice inglese Joanne Rowling esce nel 1997 ed è il primo episodio della saga di Harry Potter. In questo libro il giovane Harry, dopo aver scoperto di essere un mago, vive le sue prime avventure nella scuola di magia di Hogwarts. Il romanzo ha ottenuto un grande successo, consacrando la saga di Harry Potter come la serie di romanzi fantasy più popolare degli ultimi decenni.     ', 'assets/thumbnails/13.png', 13);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '978-88-9381-704-2', 'Harry Potter e il prigioniero di azkaban', 'Narrativa', 1, 'Salani editore ', '"Harry Potter e il prigioniero di Azkaban" vede Harry tornare a Hogwarts per il terzo anno. Affronta il fuggitivo Sirius Black mentre scopre segreti oscuri legati al suo passato.', 'assets/thumbnails/14.png', 14);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (5, 5, 'privo02', 'Il Mulino Di Po Volume 2', 'Romanzo', 1, 'Arnoldo Mondatori', 'Romanzo Storico', 'assets/thumbnails/15.png', 15);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '9788893817073', 'Harry Potter e il principe mezzosangue', 'NARRATIVA', 1, 'SALANI EDITORE', 'Il sesto anno della saga di Harry Potter inizia con le dimissioni del Ministro della Magia, Cornelius Caramell, che non aveva creduto al ritorno di Voldemort. Caramell viene sostituito dall''ex capo degli Auror, Rufus Scrimgeour. Nel frattempo Severus Piton riceve la visita di Narcissa Malfoy, madre di Draco, accompagnata dalla sorella Bellatrix. Narcissa chiede a Piton di proteggere e aiutare il figlio Draco, che è stato incaricato da Voldemort di uccidere Silente. Piton stringe con Narcissa il Voto Infrangibile, promettendo che aiuterà Draco in questa missione. Silente va a prendere Harry Potter a casa degli zii, per portarlo dai Weasley, ma prima gli chiede di accompagnarlo in una missione: deve convincere il Professore Lumacorno ad accettare l''incarico di insegnante di pozioni.', 'assets/thumbnails/16.png', 16);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 2, '978-88-9381-708-0', 'Harry Potter e i doni della morte', 'Narrativa', 1, 'Salani editore ', '"Harry Potter e i Doni della Morte" è il culmine epico della serie, dove Harry, Ron e Hermione affrontano il loro destino, cercando gli Horcrux di Voldemort per sconfiggerlo definitivamente. Con azione avvincente e momenti toccanti, questa conclusione emozionante tiene i lettori incollati fino all''ultima pagina.', 'assets/thumbnails/17.png', 17);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (6, 6, '881801627X', 'La linea d''ombra', 'Narrativa', 1, 'Rusconi libri', 'Il romanzo narra la storia di un giovane, chiamato a comandare una nave perché il suo capitano è morto. Egli deve condurre la nave in un porto dell''oceano Indiano ma la traversata nasconderà molte insidie e pericoli . Il viaggio diventa l''allegoria del percorso interiore del giovane capitano : tutte le sue insicurezze , i sensi di colpa e paure vengono superate con il coraggio e la volontà di non arrendersi alla propria morte e a quella degli altri.', 'assets/thumbnails/18.png', 18);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 8, '9788868364649', 'IT', 'NARRATIVA', 1, 'PICKWICK', 'Il romanzo è la storia di sette amici provenienti dalla fittizia città di Derry, Maine, ed è raccontata alternando due diversi periodi temporali. Dal libro sono stati tratti un''omonima miniserie televisiva nel 1990 e una trasposizione cinematografica divisa in due parti: It (2017) e It - Capitolo due (2019).', 'assets/thumbnails/19.png', 19);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (5, 9, 'privo03', 'Regina Dell''Aria e Della Notte', 'Romanzo', 1, 'G.C Sansoni', 'Romanzo Breve', 'assets/thumbnails/20.png', 20);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (36, 35, '9788894308143', 'VOCI', 'NARRATIVA', 1, 'EMIA', 'Che cosa succede se il lavoro te lo strappano via? Quali gli improvvisi cambiamenti che sei costretto ad affrontare nella vita? Diciassette racconti brevi ci fanno conoscere e amare persone a cui è stata sottratta la dignità di avere un lavoro o che stanno combattendo per mantenerne uno. Una visione di reale quotidianità che spinge il lettore in un vortice di forti emozioni e di grandi interrogativi su uno dei problemi più urgenti dei nostri giorni. Una lettura che lascia un monito per chi, operando freddamente in ossequio alle sole leggi dei numeri, non vuole rendersi conto delle possibili umane conseguenze e ci costringe a guardare con occhi diversi chi ne diviene, suo malgrado, vittima. 118 pp.', 'assets/thumbnails/29.png', 29);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (5, 11, 'privo04', 'Mezzo Miliardo', 'Romanzo', 1, 'Bietti', 'Romanzo', 'assets/thumbnails/22.png', 22);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (6, 12, '9788804668046', 'Il deserto dei Tartari', 'Narrativa', 1, 'Mondadori', 'Giovanni Drogo, un sottotenente, viene mandato in una lontana fortezza. A nord della fortezza c''è il deserto da cui si attende un''invasione dei tartari. Ma l''invasione, sempre annunciata, non avviene e l''addestramento, i turni di guardia, l''organizzazione militare, appaiono cerimoniali senza senso. Quando Drogo torna in città per una promozione, si accorge di aver perso ogni contatto con il mondo e che ormai la sua unica ragione di vita è l''inutile attesa del nemico. Tornato alla fortezza, si ammala e proprio allora accade l''evento tanto aspettato: i tartari avanzano dal deserto. Nell''emozione e nella confusione del momento, senza che lui possa prendere parte ai preparativi di difesa, Drogo muore, dimenticato da tutti.', 'assets/thumbnails/23.png', 23);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (5, 13, '9788864110776', 'Caos A Bruges', 'Romanzo', 1, 'Fazi', 'Romanzo', 'assets/thumbnails/24.png', 24);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (6, 14, '9788854172623', 'Jane Eyre', 'Narrativa', 1, 'Newton Compton Editori', 'ane Eyre è una bambina orfana che viene accolta presso i parenti dopo la morte dei genitori. In questa sua nuova famiglia Jane è resa oggetto di continui maltrattamenti da parte di una fredda zia e anche da parte degli altri bambini della casa, suoi cugini.', 'assets/thumbnails/25.png', 25);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (34, 1, 'VALE', 'VALE', 'VALE', 76, 'VALE', 'VALE', 'assets/thumbnails/26.png', 26);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 33, '9788822711717', 'Il libro dell''inquietudine', 'Romanzo', 1, 'NEWTON COMPTON EDITORI', 'Il Libro dell''inquietudine (in portoghese, Livro do Desassossego) è una delle maggiori opere dello scrittore portoghese Fernando Pessoa. Si tratta di un''opera postuma e incompiuta, oggi costituita da un''ibrida e innumerevole quantità di pagine scritte, «frammenti, tutto frammenti», come rivela Pessoa in una lettera.', 'assets/thumbnails/27.png', 27);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 36, '9788806221300', 'LA CASA IN COLLINA', 'Romanzo', 1, 'Giulio Einaudi editore', 'Cesare Pavese pubblica il romanzo La casa in collina nel 1949 insieme con Il carcere nel volume unico Prima che il gallo canti. Se Il carcere risale al periodo tra il 1938 e il 1939 e rievoca l’esperienza del confino dell’autore a Brancaleone Calabro tra il 1935 e il 1936, La casa in collina indaga le conseguenze psicologiche e sociali del secondo conflitto mondiale e della Resistenza, cui Pavese stesso non partecipa, rifugiandosi, come il protagonista, in campagna. In entrambe le opere la narrazione è dunque fortemente intrisa di elementi autobiografici, che fanno trasparire alcune costanti della poetica di Pavese: il legame disarmonico tra l’intellettuale e la realtà, il rapporto complesso con il mondo rurale delle Langhe contrapposto a quello della città, il ruolo della memoria individuale.', 'assets/thumbnails/30.png', 30);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 37, '9788894602609', 'Albert Camus la peste', 'Narrativo', 2, 'Salone internazionale del libro Torino', 'La peste è un romanzo dello scrittore francese Albert Camus del 1947. Appena pubblicata, l''opera, che rientra nella produzione di Camus definita "Ciclo della rivolta", riscosse grande successo vendendo oltre 160 000 copie nei primi due anni; ottenne tra l''altro il Prix des Critiques.', 'assets/thumbnails/31.png', 31);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 38, 'privo 09', 'I Cosacchi', 'Romanzo', 1, 'Arnoldo Mondadori', 'Libro di romanzi', 'assets/thumbnails/32.png', 32);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 39, '8804370084', 'Caino Il buono', 'Narrativa', 1, 'Arnoldo Mondadori Editore', e'Caino il buono è un libro importante, perché affronta il tema relativamente inedito dei sentimenti maschili, e del rapporto fraterno come garanzia di continuità e di solidarietà. Molti lettori vi potranno trovare una chiave d’interpretazione, se non di soluzione, dei propri conflitti familiari.
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 40, 'privo 10', 'I Quattro Libri di Lettura', 'Romanzo', 1, 'Nuova Italia', e'L\'opera è il rifacimento di un\'antologia pedagogica precedente, il Sillabario (o Abbecedario). Comprendente alcune favole di Esopo in versione libera, il rifacimento di leggende russe o indiane, racconti dei fratelli Grimm e storie vere, il testo veniva utilizzato per un apprendimento critico e attivo.[1] Contiene in 4 volumi rispettivamente 58, 65, 51 e 35 tra favole, storie e poesie.
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (37, 41, '1', 'IL QUARTIERE', 'NARRATIVA', 1, 'ARNOLDO MONDADORI EDITORE', e'Ambientato nel periodo che va dal 1932 al 1939, il romanzo narra delle peripezie amorose di un gruppo di ragazzi appartenenti ad un quartiere popolare di Firenze e più precisamente quello di Santa Croce, colti nel passaggio dall\'adolescenza alla prima giovinezza: in pratica si coglie lo sviluppo della loro educazione sentimentale e la formazione di una coscienza politica.
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 44, '9788806219352', 'Primo Levi: Se Questo é Un Uomo', 'Romanzo', 1, 'Einaudi', 'Se questo è un uomo è un''opera memorialistica di Primo Levi scritta tra il dicembre 1945 e il gennaio 1947. Rappresenta la coinvolgente ma meditata testimonianza di quanto vissuto dall''autore nel campo di concentramento di Auschwitz', 'assets/thumbnails/37.png', 37);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 45, '88-04-32055-9', 'IL GIARDINO DEI FINZI-CONTINI', 'NARRATIVA', 1, 'ARNALDO MONDADORI EDITORE', 'Il giardino dei Finzi-Contini racconta l''amore, l''amicizia, i progetti di vita e le partite a tennis di alcuni ragazzi ebrei di Ferrara perfettamente integrati nella vita della città, durante gli anni dell''università, mentre l''Italia si allea con la Germania ed entra in guerra.', 'assets/thumbnails/38.png', 38);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 46, '978-88-09-75724-0', 'MISS CHARITY', 'NARRATIVA', 1, 'GIUNTI', 'Descrizione. Charity è una bambina piena di curiosità, assetata di contatti umani, di parole e di scambi. Vuole partecipare alla vita del mondo. Purtroppo, però, una ragazzina della buona società inglese dell''800 deve tacere, non mostrarsi troppo, salvo che in chiesa.', 'assets/thumbnails/39.png', 39);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 47, '9788804658245', 'Hunger Games. La trilogia', 'Romanzo, Fantascienza', 1, 'Oscar mondadori', 'Hunger Games è una serie di romanzi di fantascienza distopica per ragazzi scritta da Suzanne Collins', 'assets/thumbnails/40.png', 40);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 50, 'PRIVO9', 'LA COMMEDIA NOSTRA', 'NARRATIVA', 3, 'Gruppo Teatrale Popolare Caprarola', 'Il gruppo teatrale popolare di caprarola ha voluto realizzare questo libro per raccogliere insieme le 5 commedie rappresentate con appassionata disinteressata partecipazione.', 'assets/thumbnails/42.png', 42);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 51, '000000000000000000000', 'SI DICE O NON SI DICE', 'NARRATIVA', 1, 'MONDADORI', 'Si dice o non si dice? di Aldo Gabrielli, nato dall’attività giornalistica e divulgativa del grande linguista, è una guida pratica ed efficace per tutti coloro che vogliono parlare e scrivere un italiano corretto ma adeguato alle necessità di comunicazione della società attuale. Parte da casi concreti e offre risposte immediate a migliaia di dubbi e domande generati dall’uso quotidiano della lingua. In questa seconda edizione ampliata e aggiornata trovano spazio i dubbi più recenti di grammatica e ortografia che derivano dalla continua evoluzione della lingua viva. Le voci, presentate in ordine alfabetico, sono facilmente reperibili. Per ognuna viene fornita una risposta immediata e chiara, seguita da un approfondimento linguistico per chi voglia saperne di più. Un libro utilissimo per imparare, ma anche piacevole da leggere, per lo stile agile e accattivante e per la ricchezza di notizie curiose, spesso risalenti ai tempi remoti in cui hanno avuto origine la nostra lingua e la nostra cultura.', 'assets/thumbnails/43.png', 43);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 52, '9788854172432', 'Il rosso e il nero', 'Romanzo, Narrativa psicologica, Romanzo di formazione', 1, 'NEWTON COMPTON EDITORI', 'Il rosso e il nero. Cronaca del 1830 è un romanzo storico dello scrittore francese Stendhal. Il protagonista, Julien Sorel, è un giovane uomo della provincia francese di modesta educazione, il quale tenta di salire la scala sociale attraverso una combinazione di talento, duro lavoro, inganno e ipocrisia. ', 'assets/thumbnails/44.png', 44);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 53, '9788866320326', 'L''AMICA GENIALE:Infanzia, adolescenza', 'Romanzo, Narrativa', 1, 'Edizioni e/o', 'L''amica geniale è un romanzo italiano di Elena Ferrante, pubblicato nel 2011. È il primo volume della serie letteraria omonima, che proseguirà con altri tre romanzi: Storia del nuovo cognome, Storia di chi fugge e di chi resta, Storia della bambina perduta.', 'assets/thumbnails/45.png', 45);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (39, 54, '9788868367305', 'Il Cacciatore di Aquiloni ', 'Romanzo', 1, 'pickwick', 'Il cacciatore di aquiloni è il primo romanzo dello scrittore afghano-americano Khaled Hosseini, pubblicato nel 2003.', 'assets/thumbnails/46.png', 46);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 53, '9788866321811', 'Storia del nuovo cognome', 'Narrativa', 1, 'Edizioni e/o', 'Storia del nuovo cognome è un romanzo del 2012 scritto da Elena Ferrante e pubblicato in Italia da E/O. È il secondo romanzo della serie L''amica geniale, preceduto da L''amica geniale del 2011 e seguito da Storia di chi fugge e di chi resta del 2013 e Storia della bambina perduta del 2014.', 'assets/thumbnails/48.png', 48);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 56, '8804301430', 'morante opere', 'NARRATIVA', 1, 'mondadori', 'Elsa Morante è stata una scrittrice, saggista, poetessa e traduttrice italiana di grande rilievo, particolarmente nota per le sue opere narrative del secondo dopoguerra. Tra le sue opere più importanti troviamo', 'assets/thumbnails/49.png', 49);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 57, '9788804701187', 'Il Vecchio E il Mare', 'Romanzo', 1, 'Mondadori', 'Un uomo può essere distrutto, ma non sconfitto.» Il vecchio e il mare è un breve romanzo dello scrittore americano Ernest Hemingway: scritto nel 1951, fu pubblicato sulla rivista Life nel 1952', 'assets/thumbnails/50.png', 50);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 58, '00000000000', 'RACCONTI E NOVELLE', 'NARRATIVA', 1, 'SANSONI EDITORE FIRENZE', 'Firenze, Sansoni, 1966, 16mo brossura con copertina illustrata a colori, pp. 569 (I capolavori, 30).', 'assets/thumbnails/51.png', 51);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 53, '9788866325512', 'Storia della bambina perduta', 'Romanzo, Narrativa', 1, 'Edizioni e/o', 'Storia della bambina perduta è un romanzo del 2014 della scrittrice Elena Ferrante e pubblicato in Italia da E/O, precisamente il 29 ottobre 2014. È il quarto e ultimo romanzo della tetralogia de L''amica geniale, preceduto da Storia di chi fugge e di chi resta, pubblicato nel 2013.', 'assets/thumbnails/52.png', 52);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 59, '000000000000000000000000000000000000000000000', 'I VIALI DI CIRCONVALLAZIONE', 'NARRATIVA', 1, 'RUSCONI EDITORE', 'I viali di circonvallazione (Les Boulevards de ceinture, 1972) è il terzo romanzo dello scrittore francese Patrick Modiano pubblicato in prima edizione il 6 ottobre del 1972 e tradotto in italiano l''anno successivo; nel ''72 vinse il premio Grand prix du roman de l''Académie française.', 'assets/thumbnails/53.png', 53);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 60, '880629363X', 'Il partigiano Johnny', 'NARRATIVA', 1, 'Einaudi', 'Il partigiano Johnny è un romanzo autobiografico incompiuto di Beppe Fenoglio pubblicato postumo nel 1968. È considerato uno dei più importanti romanzi sulla Resistenza oltre che del Novecento italiano.', 'assets/thumbnails/54.png', 54);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 61, '9788804644989', 'Ultime Lettere di Jacopo Ortis', 'Romanzo', 1, 'Oscarmondadori', 'Le Ultime lettere di Jacopo Ortis è un romanzo epistolare, composto dalle lettere che il Foscolo immagina scritte da un giovane suicida negli ultimi tempi della sua vita a un amico, Lorenzo Alderani. Questi le pubblica, aggiungendo alcuni collegamenti narrativi e descrive, alla fine, la tragica morte del protagonista.', 'assets/thumbnails/55.png', 55);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 63, '9788804666639', 'La Solitudine dei Numeri Primi', 'Romanzo', 1, 'Oscar Absolute', 'La solitudine dei numeri primi è il primo romanzo di Paolo Giordano. Romanzo di formazione, narra le vite parallele di Alice e Mattia attraverso le vicende spesso dolorose che ne segnano l''infanzia, l''adolescenza e l''età adulta', 'assets/thumbnails/57.png', 57);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 56, '978-88-06-22264-2', 'L''ISOLA DI ARTURO', 'NARRATIVA', 1, 'EINAUDI', 'L''isola di Arturo è un romanzo di Elsa Morante (Roma 1912-1985) edito da Einaudi e pubblicato nel 1957. Ha valso alla sua autrice il Premio Strega e si colloca nel filone del neorealismo.', 'assets/thumbnails/58.png', 58);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 53, '978880466324119', 'storia di chi fugge e di chi resta', 'Romanzo', 1, 'e/o', 'Storia di chi fugge e di chi resta è un romanzo del 2013 della scrittrice italiana Elena Ferrante e pubblicato in Italia da E/O. È il terzo romanzo della serie L''amica geniale, preceduto da Storia del nuovo cognome del 2012 e seguito da Storia della bambina perduta del 2014', 'assets/thumbnails/59.png', 59);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 64, '9788804665021', 'ADDIO ALLE ARMI', 'Romanzo, Narrativa, Letteratura di guerra, Realismo', 1, 'Oscar mondadori', 'Addio alle armi è una storia di amore e di guerra tra Frederic Henry, un giovane americano ricco, e Catherine Barkley. Ernest Hemingway ha sempre sognato una storia d''amore come questa e la descriveva ispirandosi alle sue esperienze sul fronte di guerra nel 1918 in Italia.', 'assets/thumbnails/60.png', 60);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 65, '978-88-200-4740-5', 'BRUNò IL COMMISSARIO FRANCESE', 'NARRATIVA', 1, 'SPERLING & KUPFER', 'Nella cittadina di St. Denis, nel Périgord, la vita scorre tranquilla per Bruno, il capo della polizia locale. Il foie gras è superbo, il vino e i formaggi eccellenti... E in paese non capita mai nulla di grave. Finché un giorno viene scoperto il cadavere sventrato di un uomo, con una svastica incisa sul petto. Da qui cominciano i problemi per il pacioso poliziotto, che tuttavia, proprio grazie ai suoi metodi poco ortodossi, risolverà il caso portando alla luce una verità che affonda le radici nel periodo tormentato della Seconda guerra mondiale. Originale e ben congegnata, la prima indagine dell''investigatore che la critica ha salutato come il "Maigret" del Périgord.', 'assets/thumbnails/61.png', 61);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 66, '9788807900983', 'Madame Bovary', 'Romanzo', 1, 'Feltrinelli', 'Madame Bovary. Mœurs de province, abbreviato normalmente in Madame Bovary, è uno dei romanzi più importanti di Gustave Flaubert, pubblicato dapprima a puntate sul giornale «La Revue de Paris» tra il 1 ottobre e il 15 dicembre 1856.', 'assets/thumbnails/62.png', 62);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 67, '9788806230142', 'Diario, Anne Frank', 'Biografia, Autobiografia, Letteratura ebraica', 1, 'Einaudi', 'Nel suo diario Anna Frank parla delle angosce, delle illusioni, dei sogni, della speranza, della distribuzione del cibo, dei turni in bagno, del cibo che non arriva, delle malattie temute e dello svolgimento della guerra.', 'assets/thumbnails/63.png', 63);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 68, '0000000', 'GLI ONESTI', 'NARRATIVA', 1, 'BOMPIANI', '(Bagnoregio, Viterbo, 1896 - Roma 1968) narratore e saggista italiano. Germanista, tenne la cattedra di letteratura tedesca nell’ateneo romano. Esordì nella narrativa con Il nome sulla sabbia (1924), pubblicando poi una serie di romanzi, racconti e prose varie che hanno al centro sottili problemi morali e psicologici, indagati secondo un’ottica cristiana e affidati a uno stile d’impronta classica. Fra i suoi libri più riusciti, soprattutto nel disegno di caratteri femminili, si ricordano: Il vento tra le case (1928), Tre storie d’amore (1931), I Villatauri (1935), Ernestina (1936), Valentina Velier (1950), Storie di bestie (1957), Gli egoisti (1959). Numerosi i volumi di saggi: Wackenroder (1927), Carossa (1947), L’arte di Thomas Mann (1956), Svevia, terra di poeti (1964), Goethe scrittore di fiabe (1966), Il senso degli altri (1968).', 'assets/thumbnails/64.png', 64);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 69, '9788822706119', 'Il Grande Gatsby', 'Romanzo', 1, 'Edizione Integrale', 'Il grande Gatsby è un romanzo di Francis Scott Fitzgerald pubblicato per la prima volta a New York il 10 aprile 1925 e definito da T.S. Eliot «il primo passo in avanti fatto dalla narrativa americana dopo Henry James', 'assets/thumbnails/65.png', 65);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 70, '97888079007863', 'I Dolori del Giovane Werther', 'Romanzo', 1, 'Demetra', ' dolori del giovane Werther è un romanzo epistolare di Johann Wolfgang Goethe pubblicato nel 1774. Il Werther appartiene all''età giovanile di Goethe ed è considerato opera simbolo del movimento dello Sturm und Drang, anticipando molti temi che saranno propri del romanticismo tedesco', 'assets/thumbnails/66.png', 66);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 77, '8804335750', 'L''INNESTO LA PATENTE L''UOMO, LA BESTIA', 'NARRATIVA', 1, 'OSCAR', 'SE SI TOGLIE L''AMARO UMANISSIMO RITRATTO DELLO IETTATORE ROSARO CHIARCHIARO NELL''ATTO UNICO LA PATENTE GLI ALTRI DUUE LAVORI QUI PRESENTATI RIPROPONGONO UN MOTIVO DRAMMATICO CHE TORNA CON INSISTENZA NELL''ISPIRAZIONE PIRANDELIANA', 'assets/thumbnails/75.png', 75);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 3, '978880466803', 'Il sentiero dei nidi di ragno', 'Narrativa', 1, 'Mondadori', 'Il sentiero dei nidi di ragno è il primo romanzo pubblicato da Italo Calvino. Il libro esce nel 1947 dall’editore Einaudi di Torino, per cui Calvino aveva iniziato a lavorare proprio in quegli anni. Il romanzo viene scritto un anno prima, nel 1946, abbastanza in fretta: Calvino sostiene di aver impiegato molto tempo solo a scrivere il primo capitolo, mentre tutto il resto della storia è stata scritta di getto. Nel 1964 esce una nuova edizione de Il sentiero dei nidi di ragno, con alcune modifiche da parte dello scrittore.', 'assets/thumbnails/72.png', 72);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 75, 'PRIVOO9', 'UN UOMO CHIAMATO PIETRO VALPEDRA ', 'NARRATIVA', 1, 'BERTANI EDITORI', 'Vedi per mia esperienza personale posso dirti che il lato piu positivo e intavolare un discorso con i passanti piu che il volantinaggio o altro', 'assets/thumbnails/73.png', 73);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 76, '8804336919', 'Il berretto a sonagli La giara Il piacere dell''onestà', 'Narrativa', 1, 'Oscar Mondadori', 'Tra l''agosto e il settembre 1916 Pirandello scrive due nuovi testi teatrali: Il berretto a sonagli e Liolà. Saranno le ultime commedie scritte in siciliano e appositamente per la messa in scena del grande attore Angelo Musco. Nel Berretto a sonagli, Beatrice si ribella all''adulterio del marito, ma il suo tentativo di smascheramento inciamperà nel paradossale tentativo del marito "cornuto", Ciampa, di nascondere la verità. Nella figura di Ciampa Pirandello riassume un tratto fondamentale della morale siciliana: la realtà non è tale finché è sottratta agli sguardi altrui, ma se questa condizione viene meno, bisogna adoperarsi perchè l''apparenza sia salva.', 'assets/thumbnails/74.png', 74);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 77, 'PRIVO11', 'LIOLA COSI E(SE VI PARE)', 'NARRATIVI', 1, 'OSCAR MONDADORI', 'E SETTEMBRE E NELLA CAMPAGNA AGRIGENTA SI SCHIACCIANO LE MANDORLE. I PERSONAGGI DI QUESTA COMMEDIA RUSTICANA , LO ZIO SIMONE LA ZIA CROCE TUZZA MITA LIOLA ENTRANO CON LA PROPRIA BIZZARRA VICENDA', 'assets/thumbnails/76.png', 76);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 73, '8811666783', 'IL SENTIERO DEI NIDI DI RAGNO', 'NARRATIVA', 1, 'GARZANTI', e'Il sentiero dei nidi di ragno è il primo romanzo di Italo Calvino. Pubblicato nel 1947 da Einaudi, è ambientato in Liguria all\'epoca della seconda guerra mondiale e della Resistenza partigiana sotto dominio nazifascista.
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 34, 'privo', 'Il velocifero', 'Narrativa', 1, 'Oscar Mondadori', 'Il "velocifero", la diligenza dei viaggi celeri del secolo scorso, fa da simbolo per la saga di una grossa e pittoresca famiglia, uomini e bestie, galleggiante sulla Milano della Belle époque.', 'assets/thumbnails/28.png', 28);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 72, '8811666813', 'LE COSMICHE', 'NARRATIVA', 1, 'GARZANTI', 'Nel 1965 esce la raccolta di racconti Le cosmicomiche. In quest’opera Italo Calvino unisce i suoi interessi scientifici a quelli letterari, accomunati da un problema di fondo: il bisogno inesausto dell''uomo di conoscere e comprendere il mondo. Il titolo mostra la capacità dell''autore di sintetizzare due generi letterari, affiancando al racconto fantascientifico la prospettiva comica.', 'assets/thumbnails/68.png', 68);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 72, 'PRIVO', 'IL CAVALIERE INESISTENTE', 'NARRATIVA', 1, 'ENAUDI', 'Agilulfo Emo Bertrandino dei Guildiverni e degli Altri di Corbentraz e Sura, cavaliere di Selimpia Citeriore e Fez, è il nome del personaggio a cui si riferisce il titolo del romanzo. Tornando a piedi all''accampamento Rambaldo scopre casualmente che il valoroso cavaliere è in realtà una donna bellissima di nome Bradamante, e se ne innamora immediatamente. Non credendo alla rivelazione, il cavaliere decide di difendere il proprio onore andando a cercare la ragazza per dimostrare che all''epoca era ancora vergine. Non considerandosi più un perfetto cavaliere perdendo la forza di volontà e la fede Agilulfo si dissolve, lasciando in eredità la sua bianca armatura a Rambaldo che, d''ora in poi, combatterà con quella ma, non essendo «perfetto» come lui, sarà in breve non più immacolata ma rovinata come tutte le armature dei normali cavalieri.', 'assets/thumbnails/69.png', 69);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 74, '9788860447258', 'Laura non c''è', 'Biografia', 1, 'FANDANGO LIBRI', 'Laura Conti muore il 25 maggio del 1993, se fosse ancora viva oggi avrebbe 100 anni ed è così che la immaginano le autrici. Sette incontri con altrettante donne con le quali discutere dei temi a lei più cari, e che ci permettono di conoscere una delle più importanti pensatrici del nostro paese.', 'assets/thumbnails/71.png', 71);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (3, 3, '978-88-04-66789-6', 'Il visconte dimezzato', 'Narrativa', 1, 'Mondadori', '"Il visconte dimezzato" di Italo Calvino racconta la storia di Visconte Medardo dimezzato da un cannone: metà gentile, metà crudele. Esplora identità e dualità in un''atmosfera fantastica e sognante.', 'assets/thumbnails/2.png', 2);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (6, 6, '9788818018950', 'Cuore di tenebra', 'Narrativa', 1, 'Rusconi libri', 'Marlow, la voce narrante di questo romanzo racconta la storia di Kurtz, misterioso personaggio che vive tra i nativi del Congo,guidandoli nelle loro scorribande per razziare avorio. A loro volta gli indigeni lo venerano come un dio in rituali dall''inaudita crudeltà. Marlow parte alla ricerca di Kurtz in quello che più che un viaggio verso una meta si rivela l''esplorazione dell'' "orrore" di una conoscenza che significa riconoscere fino in fondo il brutto di se stesso e della propria civiltà.', 'assets/thumbnails/10.png', 10);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 10, '9788804586074', 'Le Cronache di Narnia', 'NARRATIVA', 1, 'MONDADORI', e'Le storie delle Cronache di Narnia cominciano proprio da una serie di immagini nella testa del loro autore.
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (2, 42, '9788861752535', 'Lo strano caso del Dr Jekyll e Mr Hyde', 'Narrativa', 1, 'JoyBooK', 'Lo strano caso del dottor Jekyll e del signor Hyde è un racconto gotico dello scrittore Robert Louis Stevenson. Tratta la storia di un avvocato londinese, Gabriel John Utterson, il quale investiga i singolari episodi tra il suo vecchio amico, il dottor Jekyll, e il malvagio signor Hyde.', 'assets/thumbnails/36.png', 36);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 48, 'PRIVO8', 'LA CANGIARA', 'NARRATIVA', 2, 'AGNESOTTI', e'“La Congiura dei Lampugnani” è un’opera d’arte di Francesco Hayez che rappresenta un episodio storico avvenuto il 26 dicembre 1476 a Milano. Il dipinto narra il complotto ordito da Giovanni Andrea Lampugnani, Girolamo Olgiati e Carlo Visconti contro il duca Galeazzo Maria Sforza. L’opera è esposta presso la Pinacoteca di Brera a Milano e risale al periodo tra gli anni 1826 e 18291.
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (4, 55, '0', 'LA CASA TELLIER', 'NARRATIVA', 1, 'SANSONI EDITORE FIRENZE', 'Madame Tellier è una dignitosa vedova senza figli che dirige, senza provare alcun sentimento di vergogna, una casa di tolleranza in Normandia («aveva abbracciato quella professione proprio come avrebbe fatto la modista o la ricamatrice»[1]). La casa è frequentata da composti e abitudinari borghesi della cittadina (l''ex sindaco, l''armatore, il banchiere, ecc.). Madame Tellier si reca con le cinque ragazze della casa in un paesetto per la prima comunione del figlio del fratello. In chiesa, durante la funzione religiosa, le prostitute sentono nostalgia dell''antica purezza, e la loro commossa pietà diventa motivo di edificazione per i fedeli e per il celebrante («Con la vostra fede palese e la vostra pietà così viva, siete state per tutti un salutare esempio»). Terminata la cerimonia, ritornano tutte alla loro vita abituale e, dopo la breve assenza, i clienti le ritrovano più allegre e sensuali.', 'assets/thumbnails/47.png', 47);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (38, 62, 'privo104', 'Storia di un eresia', 'Romanzo', 1, 'Rusconi Editore', 'Questa raccolta di scritti postumi, curata da Franca Noventa e introdotta da un lungo saggio di Rodolfo Quadrelli, è un invito ai lettori affinché riprendano in mano i libri di questo maestro «pericoloso» per gli attuali potenti della cultura italiana. Nella seconda parte del libro si pubblica l’ultima opera di Nd venta, rimasta incompiuta e che doveva comprendere trecento lettere aperte ad amici e a letterati italiani del suo tempo, da Fortini a Debenedetti, da Vittorini a Pampaloni, da Bassani a Sereni, per citarne alcuni. Ne sono giunte a noi soltanto settanta: ma sono un’alta lezione di acutezza critica, di intuizione profetica e di humour.', 'assets/thumbnails/56.png', 56);
INSERT INTO sql1259724_5.libri (id_collocazione, id_autore, isbn, titolo, genere, quantita, casa_editrice, descrizione, thumbnail_path, id_libro) VALUES (35, 78, '9788822719706', 'La famiglia Karnowski', 'NARRATIVA', 1, 'NEWTON COMPTON EDITORI', 'Tre generazioni di ebrei, completamente assimilati alla società tedesca, si susseguono sotto la crescente e minacciosa ombra del nazismo. Questa intensa saga familiare si apre con David, il patriarca, che lascia la Polonia per trasferirsi nella civilissima Berlino e si considera "più tedesco dei tedeschi stessi"; il figlio Georg, che ha imparato dal padre a essere "ebreo in casa e tedesco fuori casa", diventa un famoso e richiestissimo medico, per poi perdere miseramente la sua credibilità, la professione, i suoi beni e ogni possibile illusione. La difficile esistenza che conduce il suo giovane figlio Jegor, eternamente in conflitto tra l''amore per la famiglia e l''odio profondo che nutre nei confronti di se stesso in quanto ebreo, conclude la parabola dei Karnovski. Affresco vivido e commovente della società ebrea in Germania tra l''inizio del secolo scorso e gli anni tra le due guerre mondiali, "La famiglia Karnovski" è riconosciuto come un grande classico della letteratura.', 'assets/thumbnails/77.png', 77);
